import java.util.*;
class reversestring{
  public static void main(String[] args)
  {
    Scanner sc=new Scanner(System.in);
    String str=sc.nextLine();
    String m="";
    for(int i=str.length()-1;i>=00;i--)
       m=m+str.charAt(i);

    System.out.println(m);   
  }
}

